import model.Customer;

import javax.servlet.*;
import javax.servlet.http.*;
import javax.servlet.annotation.*;
import java.io.IOException;
import java.util.ArrayList;

@WebServlet(name = "LoginServlet", value = "/login")
public class LoginServlet extends HttpServlet {
    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        RequestDispatcher requestDispatcher = request.getRequestDispatcher("login.html");
        requestDispatcher.forward(request,response);


    }

    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        RequestDispatcher requestDispatcher;

        ArrayList<Customer> customers = new ArrayList<>();


        customers.add(new Customer("Kien", "01-01-2003", "TH", "data:image/jpeg;base64,"));
        customers.add(new Customer("Tu", "01-01-1998", "HN", "data:image/jpeg;base64,"));
        customers.add(new Customer("Sang", "01-01-1992", "LS", "data:image/jpeg;base64,"));
        customers.add(new Customer("Mai", "01-01-1992", "HN", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQtoPMSFDBeo-Du8mWKq-ttRSCdgabj2yznbQ&usqp=CAU"));
        customers.add(new Customer("Trung", "01-01-1992", "ND", "images/trung.jpg"));

        String user = request.getParameter("username");
        String pass = request.getParameter("password");

        if(user.equalsIgnoreCase("admin") && pass.equalsIgnoreCase("admin")){
            requestDispatcher = request.getRequestDispatcher("home.jsp");
            request.setAttribute("customer", customers);
            requestDispatcher.forward(request, response);
        }else {
            requestDispatcher = request.getRequestDispatcher("login.html");
            requestDispatcher.forward(request,response);
        }

    }
}
